const userLocale =
  navigator.languages && navigator.languages.length
    ? navigator.languages[0]
    : navigator.language;

console.log({ userLocale }); // 👉️ "en-US"

// 👇️ ["en-US", "en", "de"]
console.log(navigator.languages);

const date = new Date();
const offset = date.getTimezoneOffset();
const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;

console.log({ offset });
console.log(timeZone);

function changeTimeZone(date, timeZone) {
  if (typeof date === "string") {
    return new Date(
      new Date(date).toLocaleString("eN-US", {
        timeZone,
      })
    );
  }

  return new Date(
    date.toLocaleString("en-US", {
      timeZone,
    })
  );
}

const laDate = changeTimeZone(new Date(), timeZone);
console.log({ laDate }); // 👉️ "Sun Jan 16 2022 01:22:07"

const berlinDate = changeTimeZone(new Date(), "Europe/Berlin");
console.log({ berlinDate }); // 👉️ "Sun Jan 16 2022 10:22:07"
